#! /usr/bin/env node

'use strcit';

var minimist = require('minimist');
var alienv = require('../');

/**
 * Program entrance.
 * @param argv {Array}
 */
function main(argv) {
    if (argv.length === 0) {
        console.log([
            '',
            '  Usage: alienv <config> [options]',
            ''
        ].join('\n'));
    } else {
        argv = minimist(argv);

        var config = argv._[0];
        var options = argv;

        delete options._;

        alienv(config, options);
    }
}

main(process.argv.slice(2));


